import axios from 'axios';

const API_URL = process.env.REACT_APP_API_URL || 'http://localhost:8080';

const api = axios.create({
  baseURL: API_URL,
  headers: {
    'Content-Type': 'application/json',
  },
  withCredentials: true // Important pour les cookies
});

// Configuration d'axios pour inclure le token dans les requêtes
api.interceptors.request.use(
    (config) => {
        const token = localStorage.getItem('token');
        if (token) {
            config.headers.Authorization = `Bearer ${token}`;
        }
        return config;
    },
    (error) => {
        return Promise.reject(error);
    }
);

// Gestion des erreurs de réponse
api.interceptors.response.use(
    (response) => response,
    (error) => {
        if (error.response?.status === 401) {
            localStorage.removeItem('token');
            localStorage.removeItem('user');
            window.location.href = '/login';
        }
        return Promise.reject(error);
    }
);

export const channelService = {
  getAllChannels: () => api.get('/api/channels'),
  getChannel: (id) => api.get(`/api/channels/${id}`),
  createChannel: (channel) => api.post('/api/channels', channel),
  updateChannel: (id, channel) => api.patch(`/api/channels/${id}`, channel),
  deleteChannel: (id) => api.delete(`/api/channels/${id}`),
};

export const userService = {
  getUserChannels: (userId) => api.get(`/api/users/${userId}/channels`),
};

export const messageService = {
  getMessages: (channelId) => api.get(`/api/channels/${channelId}/messages`),
  sendMessage: (channelId, message) => api.post(`/api/channels/${channelId}/messages`, message),
};

export const login = async (email, password) => {
    const response = await api.post('/api/auth/login', {
        email,
        password
    });
    return response.data;
};

export const register = async (userData) => {
    const response = await api.post('/api/auth/register', userData);
    return response.data;
};

export const logout = async () => {
    await api.post('/api/auth/logout');
    localStorage.removeItem('token');
    localStorage.removeItem('user');
    window.location.href = '/login';
};

export const getCurrentUser = async () => {
    try {
        const response = await api.get('/api/auth/me');
        return response.data;
    } catch (error) {
        return null;
    }
};

export const isAuthenticated = async () => {
    try {
        await api.get('/api/auth/me');
        return true;
    } catch (error) {
        return false;
    }
};

export default api;
