import React, { useState, useEffect } from 'react';
import { Card, Button, Form, Modal, ListGroup } from 'react-bootstrap';
import { useNavigate } from 'react-router-dom';
import { channelService } from '../services/api';

function ChatList() {
  const [channels, setChannels] = useState([]);
  const [showModal, setShowModal] = useState(false);
  const [newChannel, setNewChannel] = useState({ name: '', description: '' });
  const navigate = useNavigate();

  useEffect(() => {
    loadChannels();
  }, []);

  const loadChannels = async () => {
    try {
      const response = await channelService.getAllChannels();
      setChannels(response.data);
    } catch (error) {
      console.error('Error loading channels:', error);
    }
  };

  const handleCreateChannel = async (e) => {
    e.preventDefault();
    try {
      await channelService.createChannel(newChannel);
      setShowModal(false);
      setNewChannel({ name: '', description: '' });
      loadChannels();
    } catch (error) {
      console.error('Error creating channel:', error);
    }
  };

  const handleDeleteChannel = async (id) => {
    if (window.confirm('Are you sure you want to delete this channel?')) {
      try {
        await channelService.deleteChannel(id);
        loadChannels();
      } catch (error) {
        console.error('Error deleting channel:', error);
      }
    }
  };

  return (
    <div>
      <div className="d-flex justify-content-between align-items-center mb-4">
        <h2>Chat Channels</h2>
        <Button variant="primary" onClick={() => setShowModal(true)}>
          Create New Channel
        </Button>
      </div>

      <ListGroup>
        {channels.map((channel) => (
          <ListGroup.Item key={channel.id} className="d-flex justify-content-between align-items-center">
            <div>
              <h5>{channel.name}</h5>
              <p className="mb-0">{channel.description}</p>
            </div>
            <div>
              <Button
                variant="primary"
                className="me-2"
                onClick={() => navigate(`/chat/${channel.id}`)}
              >
                Join
              </Button>
              <Button
                variant="danger"
                onClick={() => handleDeleteChannel(channel.id)}
              >
                Delete
              </Button>
            </div>
          </ListGroup.Item>
        ))}
      </ListGroup>

      <Modal show={showModal} onHide={() => setShowModal(false)}>
        <Modal.Header closeButton>
          <Modal.Title>Create New Channel</Modal.Title>
        </Modal.Header>
        <Modal.Body>
          <Form onSubmit={handleCreateChannel}>
            <Form.Group className="mb-3">
              <Form.Label>Channel Name</Form.Label>
              <Form.Control
                type="text"
                value={newChannel.name}
                onChange={(e) => setNewChannel({ ...newChannel, name: e.target.value })}
                required
              />
            </Form.Group>
            <Form.Group className="mb-3">
              <Form.Label>Description</Form.Label>
              <Form.Control
                as="textarea"
                value={newChannel.description}
                onChange={(e) => setNewChannel({ ...newChannel, description: e.target.value })}
              />
            </Form.Group>
            <Button variant="primary" type="submit">
              Create Channel
            </Button>
          </Form>
        </Modal.Body>
      </Modal>
    </div>
  );
}

export default ChatList;
