import React, { useState, useEffect } from 'react';
import { userService } from '../services/api';
import { useParams } from 'react-router-dom';

const UserChannels = () => {
  const [channels, setChannels] = useState([]);
  const [loading, setLoading] = useState(true);
  const [error, setError] = useState(null);
  const { userId } = useParams();

  useEffect(() => {
    const loadUserChannels = async () => {
      try {
        setLoading(true);
        const response = await userService.getUserChannels(userId);
        setChannels(response.data);
        setError(null);
      } catch (err) {
        setError('Erreur lors du chargement des canaux');
        console.error('Error loading user channels:', err);
      } finally {
        setLoading(false);
      }
    };

    loadUserChannels();
  }, [userId]);

  if (loading) {
    return <div>Chargement des canaux...</div>;
  }

  if (error) {
    return <div className="error">{error}</div>;
  }

  return (
    <div className="user-channels">
      <h2>Mes Canaux</h2>
      {channels.length === 0 ? (
        <p>Aucun canal trouvé</p>
      ) : (
        <div className="channels-list">
          {channels.map((channel) => (
            <div key={channel.id} className="channel-card">
              <h3>{channel.title}</h3>
              <p>{channel.description}</p>
              <div className="channel-details">
                <span>Date: {new Date(channel.date).toLocaleDateString()}</span>
                <span>Durée: {channel.duration} minutes</span>
              </div>
            </div>
          ))}
        </div>
      )}
    </div>
  );
};

export default UserChannels;
