import axios from 'axios';

const API_URL = process.env.REACT_APP_API_URL || 'http://localhost:8080';

const api = axios.create({
  baseURL: API_URL,
  headers: {
    'Content-Type': 'application/json',
  },
  withCredentials: true // Permet l'envoi des cookies avec les requêtes
});

// Configuration d'axios pour inclure le token JWT dans les headers
api.interceptors.request.use(
    async (config) => {
        // Récupérer le token depuis le cookie
        const token = document.cookie
            .split('; ')
            .find(row => row.startsWith('jwt='))
            ?.split('=')[1];

        if (token) {
            config.headers.Authorization = `Bearer ${token}`;
        }
        return config;
    },
    (error) => {
        return Promise.reject(error);
    }
);

// Gestion des erreurs de réponse
api.interceptors.response.use(
    (response) => response,
    (error) => {
        // Ne pas rediriger si on est déjà sur la page de login ou si c'est une requête de logout
        if (error.response?.status === 401 && 
            !window.location.pathname.includes('/login') && 
            !error.config.url.includes('/auth/logout')) {
            window.location.href = '/login';
        }
        return Promise.reject(error);
    }
);

export const channelService = {
  getAllChannels: () => api.get('/api/channels'),
  getChannel: (id) => api.get(`/api/channels/${id}`),
  createChannel: (channel) => api.post('/api/channels', channel),
  updateChannel: (id, channel) => api.patch(`/api/channels/${id}`, channel),
  deleteChannel: (id) => api.delete(`/api/channels/${id}`),
};

export const userService = {
  getUserChannels: (userId) => api.get(`/api/users/${userId}/channels`),
  getCurrentUserChannels: async () => {
    const currentUser = await getCurrentUser();
    if (!currentUser) {
      throw new Error('User not authenticated');
    }
    const response = await api.get(`/api/users/${currentUser.userId}/channels`);
    return response.data;
  }
};

export const messageService = {
  getMessages: (channelId) => api.get(`/api/channels/${channelId}/messages`),
  sendMessage: (channelId, message) => api.post(`/api/channels/${channelId}/messages`, message),
};

export const login = async (email, password) => {
    const response = await api.post('/api/auth/login', {
        email,
        password
    });
    return response.data;
};

export const register = async (userData) => {
    const response = await api.post('/api/auth/register', userData);
    return response.data;
};

export const logout = async () => {
    try {
        // Supprimer le cookie JWT côté client
        document.cookie = 'jwt=; expires=Thu, 01 Jan 1970 00:00:00 UTC; path=/; domain=localhost;';

        // Supprimer le token des headers pour les prochaines requêtes
        delete api.defaults.headers.common['Authorization'];

        // Appeler l'endpoint de déconnexion sans attendre la réponse
        api.post('/api/auth/logout').catch(console.error);

        // Rediriger vers la page de login
        window.location.href = '/login';
    } catch (error) {
        console.error('Erreur lors de la déconnexion:', error);
        // Même en cas d'erreur, on supprime le cookie et on redirige
        document.cookie = 'jwt=; expires=Thu, 01 Jan 1970 00:00:00 UTC; path=/; domain=localhost;';
        window.location.href = '/login';
    }
};

export const getCurrentUser = async () => {
    try {
        const response = await api.get('/api/auth/me');
        return response.data;
    } catch (error) {
        return null;
    }
};

export const isAuthenticated = async () => {
    try {
        await api.get('/api/auth/me');
        return true;
    } catch (error) {
        return false;
    }
};

export default api;
