import React, { useState, useEffect } from 'react';
import { Link, useNavigate, useLocation } from 'react-router-dom';
import { Navbar, Nav, Container, Button } from 'react-bootstrap';
import { getCurrentUser, logout } from '../services/api';

const Navigation = () => {
    const navigate = useNavigate();
    const location = useLocation();
    const [user, setUser] = useState(null);
    const [loading, setLoading] = useState(true);

    useEffect(() => {
        // Ne pas faire l'appel à auth/me sur les pages de login et register
        if (location.pathname === '/login' || location.pathname === '/register') {
            setLoading(false);
            return;
        }

        const fetchUser = async () => {
            try {
                const userData = await getCurrentUser();
                setUser(userData);
            } catch (error) {
                setUser(null);
            } finally {
                setLoading(false);
            }
        };

        fetchUser();
    }, [location.pathname]);

    const handleLogout = async () => {
        try {
            await logout();
            setUser(null);
            navigate('/login');
        } catch (error) {
            console.error('Erreur lors de la déconnexion:', error);
        }
    };

    // Ne pas afficher la navigation sur les pages de login et register
    if (location.pathname === '/login' || location.pathname === '/register') {
        return null;
    }

    if (loading) {
        return null; // ou un spinner si vous préférez
    }

    return (
        <Navbar bg="dark" variant="dark" expand="lg">
            <Container>
                <Navbar.Brand as={Link} to="/">Chat App</Navbar.Brand>
                <Navbar.Toggle aria-controls="basic-navbar-nav" />
                <Navbar.Collapse id="basic-navbar-nav">
                    <Nav className="me-auto">
                        {user ? (
                            <>
                                <Nav.Link as={Link} to="/channels">Mes Canaux</Nav.Link>
                                <Nav.Link as={Link} to="/chat">Chat</Nav.Link>
                            </>
                        ) : (
                            <>
                                <Nav.Link as={Link} to="/login">Connexion</Nav.Link>
                                <Nav.Link as={Link} to="/register">Inscription</Nav.Link>
                            </>
                        )}
                    </Nav>
                    {user && (
                        <Nav className="align-items-center">
                            <Navbar.Text className="me-3 text-light">
                                Connecté en tant que: {user.email}
                            </Navbar.Text>
                            <Button 
                                variant="outline-light" 
                                onClick={handleLogout}
                                className="logout-button"
                            >
                                Déconnexion
                            </Button>
                        </Nav>
                    )}
                </Navbar.Collapse>
            </Container>
        </Navbar>
    );
};

export default Navigation;
