import React, { useState, useEffect } from 'react';
import { Card, Button, Form, Modal, ListGroup } from 'react-bootstrap';
import { useNavigate } from 'react-router-dom';
import { channelService } from '../services/api';

function ChatList() {
  const [channels, setChannels] = useState([]);
  const [showModal, setShowModal] = useState(false);
  const [newChannel, setNewChannel] = useState({
    title: '',
    description: '',
    date: '',
    duration: 1,
    ownerId: 1 // À remplacer par l'ID de l'utilisateur connecté
  });
  const navigate = useNavigate();

  useEffect(() => {
    loadChannels();
  }, []);

  const loadChannels = async () => {
    try {
      const response = await channelService.getAllChannels();
      setChannels(response.data);
    } catch (error) {
      console.error('Error loading channels:', error);
    }
  };

  const handleCreateChannel = async (e) => {
    e.preventDefault();
    try {
      // Convertir la date en format ISO
      const channelData = {
        ...newChannel,
        date: new Date(newChannel.date).toISOString()
      };

      await channelService.createChannel(channelData);
      setShowModal(false);
      setNewChannel({
        title: '',
        description: '',
        date: '',
        duration: 1,
        ownerId: 1
      });
      loadChannels();
    } catch (error) {
      console.error('Error creating channel:', error);
    }
  };

  const handleDeleteChannel = async (id) => {
    if (window.confirm('Are you sure you want to delete this channel?')) {
      try {
        await channelService.deleteChannel(id);
        loadChannels();
      } catch (error) {
        console.error('Error deleting channel:', error);
      }
    }
  };

  const formatDate = (dateString) => {
    return new Date(dateString).toLocaleString();
  };

  const formatDuration = (duration) => {
    // Si duration est un objet avec toHours, l'utiliser
    if (duration && typeof duration.toHours === 'function') {
      return `${duration.toHours()}h`;
    }
    // Sinon, supposer que c'est un nombre d'heures
    return `${duration}h`;
  };

  return (
    <div>
      <div className="d-flex justify-content-between align-items-center mb-4">
        <h2>Chat Channels</h2>
        <Button variant="primary" onClick={() => setShowModal(true)}>
          Create New Channel
        </Button>
      </div>

      <ListGroup>
        {channels.map((channel) => (
          <ListGroup.Item key={channel.channelId} className="d-flex justify-content-between align-items-center">
            <div>
              <h5>{channel.title}</h5>
              <p className="mb-0">{channel.description}</p>
              <small className="text-muted">
                Date: {formatDate(channel.date)} | Duration: {formatDuration(channel.duration)}
              </small>
            </div>
            <div>
              <Button
                variant="primary"
                className="me-2"
                onClick={() => navigate(`/chat/${channel.channelId}`)}
              >
                Join
              </Button>
              <Button
                variant="danger"
                onClick={() => handleDeleteChannel(channel.channelId)}
              >
                Delete
              </Button>
            </div>
          </ListGroup.Item>
        ))}
      </ListGroup>

      <Modal show={showModal} onHide={() => setShowModal(false)}>
        <Modal.Header closeButton>
          <Modal.Title>Create New Channel</Modal.Title>
        </Modal.Header>
        <Modal.Body>
          <Form onSubmit={handleCreateChannel}>
            <Form.Group className="mb-3">
              <Form.Label>Channel Title</Form.Label>
              <Form.Control
                type="text"
                value={newChannel.title}
                onChange={(e) => setNewChannel({ ...newChannel, title: e.target.value })}
                required
              />
            </Form.Group>
            <Form.Group className="mb-3">
              <Form.Label>Description</Form.Label>
              <Form.Control
                as="textarea"
                value={newChannel.description}
                onChange={(e) => setNewChannel({ ...newChannel, description: e.target.value })}
                required
              />
            </Form.Group>
            <Form.Group className="mb-3">
              <Form.Label>Date and Time</Form.Label>
              <Form.Control
                type="datetime-local"
                value={newChannel.date}
                onChange={(e) => setNewChannel({ ...newChannel, date: e.target.value })}
                required
              />
            </Form.Group>
            <Form.Group className="mb-3">
              <Form.Label>Duration (hours)</Form.Label>
              <Form.Control
                type="number"
                min="1"
                value={newChannel.duration}
                onChange={(e) => setNewChannel({ ...newChannel, duration: parseInt(e.target.value) })}
                required
              />
            </Form.Group>
            <Button variant="primary" type="submit">
              Create Channel
            </Button>
          </Form>
        </Modal.Body>
      </Modal>
    </div>
  );
}

export default ChatList;
